.nav {
  display: flex;
  justify-content: flex-start;
  align-items: stretch;
  flex-direction: row;
  width: 100%;
  padding: 0 4rem;
}
.navList {
  display: flex;
  justify-content: flex-end;
  align-items: stretch;
  flex-direction: row;
  flex-grow: 1;
  list-style: none;
  padding: 0rem;
  transition: all 0.15s ease-out;
}
.navItem {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  margin: 0 1rem;
}
.navLink {
  color: #FFF;
  font-weight: bold;
  pointer-events: all;
  transition: all 0.15s ease-out;
}
.navLink:hover, .navLink.active {
  color: #FFD544;
}
.nav.mainNav {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100%;
  max-height: 5rem;
  z-index: 10;
  transition: all 0.15s ease-out;
  padding: 1rem 4rem;
  pointer-events: none;
}
.nav.mainNav .navLink {
  text-transform: lowercase;
}
@media only screen and (min-device-width:320px) and (max-device-width:736px), (min-width:320px) and (max-width:736px) {
  .nav.mainNav {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: row;
  }
  .nav.mainNav .navList {
    position: absolute;
    top: 0;
    right: -100%;
    height: 100vh;
    width: min(50vw, 260px);
    background-color: #212529;
    display: flex;
    justify-content: flex-start;
    align-items: flex-end;
    flex-direction: row;
    flex-direction: column;
    padding: 4rem 0;
    box-shadow: -1rem 0 1rem rgba(26, 26, 26, 0.5);
  }
  .nav.mainNav .navItem {
    width: 100%;
    margin: 0;
  }
  .nav.mainNav .navLink {
    display: flex;
    justify-content: flex-end;
    align-items: center;
    flex-direction: row;
    width: 100%;
    padding: 1rem 2rem;
  }
}

.burger {
  position: absolute;
  right: 1rem;
  width: 2.2rem;
  height: 2.2rem;
  z-index: 2;
  transition: transform 0.15s ease-out;
  display: none;
  pointer-events: all;
}
@media only screen and (min-device-width:320px) and (max-device-width:736px), (min-width:320px) and (max-width:736px) {
  .burger {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: row;
    flex-wrap: wrap;
  }
}
.burger span {
  position: absolute;
  width: 2rem;
  height: 0.3rem;
  background-color: #FFF;
  display: block;
  transition: all 0.15s ease-out;
}
.burger span:nth-of-type(1) {
  transform: translateY(-0.65rem);
}
.burger span:nth-of-type(3) {
  transform: translateY(0.65rem);
}

.branding {
  display: flex;
  justify-content: flex-start;
  align-items: center;
  flex-direction: row;
  position: relative;
  pointer-events: all;
  height: inherit;
}
.branding img {
  width: auto;
  height: 100%;
  transition: all 0.15s ease-out;
}
.branding img.dark {
  position: absolute;
  opacity: 0;
}

.page-scrolled .mainNav {
  background-color: #FFF !important;
  max-height: calc(5rem - 5rem / 4) !important;
  box-shadow: 0 1rem 1rem rgba(26, 26, 26, 0.1);
}
.page-scrolled .branding img {
  opacity: 0 !important;
}
.page-scrolled .branding img.dark {
  opacity: 1 !important;
}
.page-scrolled .navLink {
  color: #212529;
}
.page-scrolled .navLink:hover, .page-scrolled .navLink.active {
  color: #FFD544;
}
.page-scrolled .burger span {
  background-color: #212529;
}

@media only screen and (min-device-width:320px) and (max-device-width:736px), (min-width:320px) and (max-width:736px) {
  .menu-open .mainNav .navList {
    right: 0;
  }
}
.menu-open .burger {
  top: 1rem;
  transform: scale(0.5);
}
.menu-open .burger span:nth-of-type(1) {
  transform: translateY(0rem) rotate(-45deg);
}
.menu-open .burger span:nth-of-type(2) {
  transform: scaleX(0);
}
.menu-open .burger span:nth-of-type(3) {
  transform: translateY(0rem) rotate(45deg);
}

.page-scrolled.menu-open .burger {
  top: 1rem;
}
.page-scrolled.menu-open .burger span {
  background-color: #FFF;
}
@media only screen and (min-device-width:320px) and (max-device-width:736px), (min-width:320px) and (max-width:736px) {
  .page-scrolled.menu-open .mainNav .navLink {
    color: #FFF;
  }
  .page-scrolled.menu-open .mainNav .navLink:hover, .page-scrolled.menu-open .mainNav .navLink.active {
    color: #FFD544;
  }
}

.section {
