  position: relative;
  min-height: 100vh;
  background-color: #1A1A1A;
  line-height: 1.4;
}
.section:first-of-type {
  padding-top: 70px;
}
.section.auto {
  min-height: auto;
}
.section.overlay:before {
  content: "";
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
  background-color: rgba(26, 20, 35, 0.85);
