}
.section--hero .heroContent {
  width: 100%;
  max-width: 40rem;
  padding: 2rem 4rem;
  background-color: rgba(228, 29, 165, 0.5);
}
.section--about {
  background-color: #6c757d;
  background-image: url("/c01f0018112ca0ecee07b58da6575aee.jpg");
  background-size: cover;
  background-position: center center;
  background-attachment: fixed;
  color: #FFF;
}
.section--about .bigLogoContainer {
  display: flex;
  justify-content: flex-start;
  align-items: center;
  flex-direction: row;
}
.section--about .bigLogo {
  width: min(300px, 100%);
  height: auto;
  border-radius: 50%;
  background-color: rgba(255, 255, 255, 0.15);
