* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

a {
  text-decoration: none;
  color: #FFD544;
  transition: color 0.15s ease-out;
}
a:hover {
  color: #ffe077;
}

img {
  width: 100%;
  height: auto;
}

body {
  width: 100vw;
  height: 100vh;
  font-size: 16px;
  background-color: #353535;
  background-image: linear-gradient(to bottom, rgba(0, 0, 0, 0.9), rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.9));
  color: #6c757d;
  font-family: "Raleway", sans-serif;
  overflow-y: hidden;
  overflow-x: hidden;
}
body .animate__animated {
  animation-duration: 0.3s !important;
  animation-delay: 0.3s !important;
}
body .animated {
  opacity: 0;
  animation-fill-mode: both;
}
body *[class*=animate__] {
  animation-fill-mode: both;
  animation-delay: 0ms;
  animation-duration: 500ms;
}
body.transitioning .main .contentWrapper {
  overflow-y: hidden;
  overflow-x: hidden;
}
body.transitioning .ps__rail-y, body.transitioning .ps__rail-x {
  display: none;
}
body.transitioning .mainNav .navLink {
  pointer-events: none;
}

.main {
  perspective: 100vw;
}
.main .content {
  perspective: 100vw;
  transform-origin: center center;
  position: fixed;
  overflow: hidden;
}
.main .contentWrapper {
  position: relative;
  width: 100vw;
  height: 100vh;
  overflow-y: hidden;
  overflow-x: hidden;
}

.transitionOverlay {
  position: absolute;
  z-index: 3;
  display: none;
  pointer-events: none;
  height: auto;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
}
.transitionOverlay__cover {
  content: "";
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 5;
  background-color: #1a1423;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 5;
}
.transitionOverlay__spin {
  width: 80px;
  height: 80px;
  display: none;
  opacity: 0;
  border-radius: 50%;
  background-color: #FFF;
  background-image: url(/1274a4761dfad77a7a4a1c4ee50f6a79.png);
  background-size: 80%;
  background-position: center center;
  background-repeat: no-repeat;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  transform-origin: center center;
  z-index: 6;
  margin-top: -3rem;
}

.footer {
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100vw;
  padding: 0.5rem 1rem;
  background-color: rgba(255, 255, 255, 0.1);
  color: #1A1A1A;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: row;
  z-index: 2;
}
.footer p {
  font-size: 0.5rem;
  color: #6c757d;
  text-transform: uppercase;
  letter-spacing: 0.25rem;
  margin: 0;
}
.footer a {
  color: #FFF;
}
.footer a:hover {
  color: #FFD544;
}

.ps__rail-y, .ps__rail-x {
  z-index: 1000;
}

