sources.style\\common\\_button.scss:
.btn {
  display: inline-block;
  padding: 1rem 2rem;
  width: auto;
  font-weight: bold;
  transition: background-color 0.15s ease-out, border-color 0.15s ease-out, color 0.15s ease-out;
  border-width: 1px;
  border-style: solid;
  border-color: rgba($white, 0.5);
  color: $white;
  background-color: rgba($white, 0.05);
  position: relative;

  margin: 1rem 0;
  &:not(:last-of-type) {
    margin-right: 0.5rem;
  }
  
  &:before {
    content: '';
    display: block;
    width: 0;
    height: 100%;
    position: absolute;
    z-index: -1;
    top: 0;
    left: 0;
    background-color: $white;
    transition: background-color 0.15s ease-out, width 0.15s ease-out;
  }

  &:hover {
    color: $darkgray;
    border-color: rgba($white, 1);
    &:before {
      width: 100%;
    }
  }

  // .btn--primary
  &--primary {
    border-color: $primary;
    color: lighten($primary, 20%);
    background-color: rgba($primary, 0.05);
    &:before {
      background-color: $primary;
    }
    &:hover {
      color: $white;
      border-color: rgba($primary, 1);
      &:before {
        background-color: $primary;
      }
    }
  }

  // .btn--secondary
  &--secondary {
    border-color: $secondary;
    color: lighten($secondary, 20%);
    background-color: rgba($secondary, 0.05);
    &:before {
      background-color: $secondary;
    }
    &:hover {
      color: $white;
      border-color: rgba($secondary, 1);
      &:before {
        background-color: $secondary;
      }
    }
  }

  // .btn--tertiary
  &--tertiary {
    border-color: $tertiary;
    color: lighten($tertiary, 20%);
    background-color: rgba($tertiary, 0.05);
    &:before {
      background-color: $tertiary;
    }
    &:hover {
      color: $white;
      border-color: rgba($tertiary, 1);
      &:before {
        background-color: $tertiary;
      }
    }
  }

  // .btn--success
  &--success {
    border-color: $success;
    color: lighten($success, 20%);
    background-color: rgba($success, 0.05);
    &:before {
      background-color: $success;
    }
    &:hover {
      color: $white;
      border-color: rgba($success, 1);
      &:before {
        background-color: $success;
      }
    }
  }

  // .btn--info
  &--info {
    border-color: $info;
    color: lighten($info, 20%);
    background-color: rgba($info, 0.05);
    &:before {
      background-color: $info;
    }
    &:hover {
      color: $white;
      border-color: rgba($info, 1);
      &:before {
        background-color: $info;
      }
    }
  }

  // .btn--warning
  &--warning {
    border-color: $warning;
    color: lighten($warning, 20%);
    background-color: rgba($warning, 0.05);
    &:before {
      background-color: $warning;
    }
    &:hover {
      color: $white;
      border-color: rgba($warning, 1);
      &:before {
        background-color: $warning;
      }
    }
  }

  // .btn--danger
  &--danger {
    border-color: $danger;
    color: lighten($danger, 20%);
    background-color: rgba($danger, 0.05);
    &:before {
      background-color: $danger;
    }
    &:hover {
      color: $white;
      border-color: rgba($danger, 1);
      &:before {
        background-color: $danger;
      }
    }
  }
}

sources.style\\util\\_settings.scss:
// ========================
// THEME COLORS
// ========================
$primary: #FFD544;
$secondary: #E41DA5;
$tertiary: #902BEE;
$accent: #3a0ca3;


// ========================
// BASIC COLORS
// ========================
$black: #1A1A1A;
$gray: #6c757d;
$lightgray: #adb5bd;
$darkgray: #212529;

$red: #FF4444;
$orange: #F98A1E;
$yellow: #FFD544;
$green: #30b407;
$jade: #008B98;
$teal: #1FC8B6;
$blue: #0060FF;
$indigo: #3A13B7;
$purple: #902BEE;
$violet: #E41DA5;
$white: #FFF;


// ========================
// SYSTEM COLORS
// ========================
$success: #35B035;
$warning: #F98A1E;
$danger: #AA2225;
$info: #4680FE;
$bgcolor: #353535;
$overlay: #1a1423;


// ========================
// SIZES
// ========================
$font-size: 16px;
$navHeight: 5rem;


// ========================
// MEDIA QUERY
// ========================
$responsive-laptop: 'only screen and (max-width:1440px)';
$responsive-tablet: 'only screen and (min-device-width:768px) and (max-device-width:1024px), (min-width: 768px) and (max-width: 1024px)';
$responsive-phone: 'only screen and (min-device-width:320px) and (max-device-width:736px), (min-width:320px) and (max-width:736px)';
$responsive-portrait: 'and (orientation: portrait)';
$responsive-landscape: 'and (orientation: landscape)';

sources.style\\common\\_grid.scss:
//----- Extends
%clearfix:after {
	display:block;
	clear:both;
	content:'';
}

//----- Grid System
$grid-spacing: 1rem;

.row {
  @include flex(flex-start, flex-start);
  flex-wrap: wrap;

  margin-left: (0 - $grid-spacing);
  margin-right: (0 - $grid-spacing);
  margin-top: ($grid-spacing * 2);
  margin-bottom: ($grid-spacing * 2);
}

[class^="col-"] {
  flex-shrink: 0;
  margin: 0 $grid-spacing;
  margin-bottom: ($grid-spacing * 2);

  @media #{$responsive-phone} {
    width: calc(100% - calc(#{$grid-spacing} * 2)) !important;
    margin: $grid-spacing !important;
  }
}

//----- Columns
@for $i from 1 through 12 {
  .col-#{$i} {
    width: calc( calc(100% / (12 / #{$i})) - calc(#{$grid-spacing} * 2) );
  }
}
// .col-1 { width: calc(calc(100% / 12) - calc(#{$grid-spacing} * 2)); }
// .col-2 { width: calc(calc(100% / 6) - calc(#{$grid-spacing} * 2)); }
// .col-3 { width: calc(calc(100% / 4) - calc(#{$grid-spacing} * 2)); }
// .col-4 { width: calc(calc(100% / 3) - calc(#{$grid-spacing} * 2)); }
// .col-5 { width: calc(calc(100% / 2.4) - calc(#{$grid-spacing} * 2)); }
// .col-6 { width: calc(calc(100% / 2) - calc(#{$grid-spacing} * 2)); }
// .col-7 { width: calc(calc(100% / 1.714285714285714) - calc(#{$grid-spacing} * 2)); }
// .col-8 { width: calc(calc(100% / 1.5) - calc(#{$grid-spacing} * 2)); }
// .col-9 { width: calc(calc(100% / 1.333333333333333) - calc(#{$grid-spacing} * 2)); }
// .col-10 { width: calc(calc(100% / 1.2) - calc(#{$grid-spacing} * 2)); }
// .col-11 { width: calc(calc(100% / 1.090909090909091) - calc(#{$grid-spacing} * 2)); }
// .col-12 { width: calc(100%); }

//----- Offsets
@for $i from 1 through 12 {
  .off-#{$i} {
    margin-left: calc( calc(100% / (12 / #{$i})) + calc(#{$grid-spacing} * 1) );
  }
}

sources.style\\util\\_mixins.scss:
@mixin materialIcon($icon) {
  content: $icon;
  font-family: "Material Icons";
  font-weight: normal;
  font-style: normal;
  font-size: 24px; /* Preferred icon size */
  display: inline-block;
  line-height: 1;
  text-transform: none;
  letter-spacing: normal;
  word-wrap: normal;
  white-space: nowrap;
  direction: ltr;

  /* Support for all WebKit browsers. */
  -webkit-font-smoothing: antialiased;
  /* Support for Safari and Chrome. */
  text-rendering: optimizeLegibility;

  /* Support for Firefox. */
  -moz-osx-font-smoothing: grayscale;

  /* Support for IE. */
  font-feature-settings: "liga";
}

@mixin flex($hAlign: flex-start, $vAlign: flex-start, $direction: row, $impt: "") {
  display: flex #{$impt};
  justify-content: $hAlign #{$impt};
  align-items: $vAlign #{$impt};
  flex-direction: $direction;
}

$scroller-track-color-inactive: #eeeeee;
$scroller-track-color-active: #e0e0e0;
$scroller-thumb-color-inactive: #9e9e9e;
$scroller-thumb-color-active: $primary;

@mixin applyScrollbar(
  $scrollbar-track-bg: $scroller-track-color-inactive,
  $scrollbar-thumb-bg: $scroller-thumb-color-inactive,
  $scrollbar-track-bg-active: $scroller-track-color-active,
  $scrollbar-thumb-bg-active: $scroller-thumb-color-active,
  $scrollbar-curve: 8px,
  $scrollbar-size: 8px
) {
  width: 100%;
  overflow-y: auto;
  overflow-x: hidden;
  position: relative;

  &::-webkit-scrollbar {
    width: $scrollbar-size;
    height: $scrollbar-size;
    background-color: inherit;
  }

  &::-webkit-scrollbar-track {
    background-color: $scrollbar-track-bg;
    border-radius: $scrollbar-curve;
  }

  &::-webkit-scrollbar-thumb {
    background-color: $scrollbar-thumb-bg;
    border-radius: 4em;
  }
  &:hover {
    &::-webkit-scrollbar-track {
      background-color: $scrollbar-track-bg-active;
    }

    &::-webkit-scrollbar-thumb {
      background-color: $scrollbar-thumb-bg-active;
    }
  }
}

@mixin overlay($z:2, $opacity: 0.5, $color: $black, $pos: absolute) {
  content: '';
  display: block;
  position: $pos;
  top: 0;
  left: 0;
  @if ($pos == 'fixed') {
    width: 100vw;
    height: 100vh;
  }
  @else {
    width: 100%;
    height: 100%;
  }
  z-index: $z;
  background-color: rgba($color, $opacity);
}

sources.style\\common\\_nav.scss:
.nav {
  @include flex(flex-start, stretch);
  width: 100%;
  padding: 0 4rem;

  // .navList
  &List {
    @include flex(flex-end, stretch);
    flex-grow: 1;
    list-style: none;
    padding: 0rem;
    transition: all 0.15s ease-out;
  }

  // .navItem
  &Item {
    @include flex(center, center);
    margin: 0 1rem;
  }

  // .navLink
  &Link {
    color: $white;
    font-weight: bold;
    pointer-events: all;
    transition: all 0.15s ease-out;

    &:hover, &.active {
      color: $primary;
    }
  }

  &.mainNav {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100%;
    max-height: $navHeight;
    z-index: 10;
    transition: all 0.15s ease-out;
    padding: 1rem 4rem;
    pointer-events: none;

    .navLink {
      text-transform: lowercase;
    }

    @media #{$responsive-phone} {
      @include flex(center, center);
      .nav {
        // .navList
        &List {
          position: absolute;
          top: 0;
          right: -100%;
          height: 100vh;
          width: min(50vw, 260px);
          background-color: $darkgray;
          @include flex(flex-start, flex-end);
          flex-direction: column;
          padding: 4rem 0;
          box-shadow: -1rem 0 1rem rgba($black, 0.5);
        }
        // .navItem
        &Item {
          width: 100%;
          margin: 0;
        }
        // .navLink
        &Link {
          @include flex(flex-end, center);
          width: 100%;
          padding: 1rem 2rem;
        }
      }
    }
  }
}

.burger {
  position: absolute;
  right: 1rem;
  width: 2.2rem;
  height: 2.2rem;
  z-index: 2;
  transition: transform 0.15s ease-out;
  display: none;
  pointer-events: all;
  @media #{$responsive-phone} {
    @include flex(center, center);
    flex-wrap: wrap;
  }

  span {
    position: absolute;
    width: 2rem;
    height: 0.3rem;
    background-color: $white;
    display: block;
    transition: all 0.15s ease-out;

    &:nth-of-type(1) {
      transform: translateY(-0.65rem);
    }
    &:nth-of-type(3) {
      transform: translateY(0.65rem);
    }
  }
}

.branding {
  @include flex(flex-start, center);
  position: relative;
  pointer-events: all;
  height: inherit;
  img {
    width: auto;
    height: 100%;
    transition: all 0.15s ease-out;

    &.dark {
      position: absolute;
      opacity: 0;
    }
  }
}

.page-scrolled {
  .mainNav {
    background-color: $white !important;
    max-height: calc(#{$navHeight} - #{$navHeight} / 4) !important;
    box-shadow: 0 1rem 1rem rgba($black, 0.1);
  }
  .branding {
    img {
      opacity: 0 !important;
      &.dark {
        opacity: 1 !important;
      }
    }
  }
  .nav {
    &Link {
      color: $darkgray;
      &:hover, &.active {
        color: $primary;
      }
    }
  }

  .burger {
    span {
      background-color: $darkgray;
    }
  }
}

.menu-open {
  .mainNav {
    @media #{$responsive-phone} {
      .nav {
        &List {
          right: 0;
        }
      }
    }
  }

  .burger {
    top: 1rem;
    transform: scale(0.5);
    span {
      &:nth-of-type(1) {
        transform: translateY(0rem) rotate(-45deg);
      }
      &:nth-of-type(2) {
        transform: scaleX(0);
      }
      &:nth-of-type(3) {
        transform: translateY(0rem) rotate(45deg);
      }
    }
  }
}


.page-scrolled.menu-open {
  .burger {
    top: 1rem;
    span {
      background-color: $white;
    }
  }
  .mainNav {
    .navLink {
      @media #{$responsive-phone} {
        color: $white;

        &:hover, &.active {
          color: $primary;
        }
      }
    }
  }
}

sources.style\\common\\_section.scss:
.section {
  @include flex(flex-start, stretch);
  width: 100%;
  position: relative;
  min-height: 100vh;
  background-color: $black;
  line-height: 1.4;

  &:first-of-type {
    padding-top: 70px;
  }

  &.auto {
    min-height: auto;
  }

  &.overlay {
    &:before {
      @include overlay(1, 0.85, $overlay, absolute);
    }
  }

  // .sectionWrapper
  &Wrapper {
    width: 100%;
    position: relative;
    padding: 4rem;
    z-index: 2;
  }

  // .section--hero
  &--hero {
    color: $white;
    min-height: 100vh;

    background-color: $gray;
    background-image: url('/image/hero.jpg');
    background-size: cover;
    background-position: center center;
    background-attachment: fixed;
    padding-top: 0px !important;

    .sectionWrapper {
      @include flex(center, center);
      width: 100%;
    }

    .heroContent {
      width: 100%;
      max-width: 40rem;
      padding: 2rem 4rem;
      background-color: rgba($violet, 0.5);
    }
  }

  // .section--about
  &--about {
    background-color: $gray;
    background-image: url('/image/about_bg.jpg');
    background-size: cover;
    background-position: center center;
    background-attachment: fixed;
    color: $white;

    .bigLogoContainer {
      @include flex(flex-start, center);
    }
    .bigLogo {
      width: min(300px, 100%);
      height: auto;
      border-radius: 50%;
      background-color: rgba($white, 0.15);
      object-fit: contain;
      padding: 2rem;
    }
  }

  // .section--portfolio
  &--portfolio {
    background-color: $gray;
    background-image: url('/image/portfolio_bg.jpg');
    background-size: cover;
    background-position: center center;
    background-attachment: fixed;
    color: $white;
  }

  // .section--contact
  &--contact {
    background-color: $gray;
    background-image: url('/image/contact_bg.jpg');
    background-size: cover;
    background-position: center center;
    background-attachment: fixed;
    color: $white;

    h1 {
      color: $white;
    }
  
    p {
      color: $lightgray;
    }

    .contactForm {
      padding: 2rem;
      background-color: rgba($black, 0.5);
    }
  }

  h1, h2, h3, h4, h5, h6 {
    color: $white;
  }

  p {
    color: $lightgray;
  }
}

sources.style\\_layout.scss:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

a {
  text-decoration: none;
  color: $yellow;
  transition: color 0.15s ease-out;
  &:hover {
    color: lighten($yellow, 10%);
  }
}

img {
  width: 100%;
  height: auto;
}

body {
  width: 100vw;
  height: 100vh;
  font-size: $font-size;
  background-color: $bgcolor;
  background-image: linear-gradient(to bottom, rgba(0, 0, 0, 0.9), rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.9));
  color: $gray;
  font-family: 'Raleway', sans-serif;
  overflow-y: hidden;
  overflow-x: hidden;

  .animate__animated {
    animation-duration: 0.3s !important;
    animation-delay: 0.3s !important;
  }

  .animated {
    opacity: 0;
    animation-fill-mode: both;
  }
  *[class*=animate__] {
    animation-fill-mode: both;
    animation-delay: 0ms;
    animation-duration: 500ms;
  }

  &.transitioning {
    .main {
      .contentWrapper {
        overflow-y: hidden;
        overflow-x: hidden;
      }
    }

    // .transitionOverlay {
    //   opacity: 1;
    //   // transition: none;
    // }

    .ps__rail-y, .ps__rail-x {
      display: none;
    }

    .mainNav {
      .navLink {
        pointer-events: none;
      }
    }
  }
}

.main {
  perspective: 100vw;
  .content {
    perspective: 100vw;
    transform-origin: center center;
    position: fixed;
    overflow: hidden;
  }
  .contentWrapper {
    position: relative;
    width: 100vw;
    height: 100vh;
    overflow-y: hidden;
    overflow-x: hidden;
  }
}

.transitionOverlay {
  position: absolute;
  z-index: 3;
  display: none;
  pointer-events: none;
  height: auto;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  
  // .transitionOverlay__cover
  &__cover {
    @include overlay(5, 1, $overlay);
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 5;
  }

  // .transitionOverlay__spin
  &__spin {
    width: 80px;
    height: 80px;
    display: none;
    opacity: 0;

    border-radius: 50%;
    background-color: $white;
    background-image: url(/image/icon.png);
    background-size: 80%;
    background-position: center center;
    background-repeat: no-repeat;

    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    transform-origin: center center;
    z-index: 6;
    margin-top: -3rem;
  }
}

.footer {
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100vw;
  padding: 0.5rem 1rem;
  background-color: rgba(255, 255, 255, 0.1);
  color: $black;
  @include flex(center, center);
  z-index: 2;

  p {
    font-size: 0.5rem;
    color: $gray;
    text-transform: uppercase;
    letter-spacing: 0.25rem;
    margin: 0;
  }

  a {
    color: $white;

    &:hover {
      color: $yellow;
    }
  }
}

.ps__rail-y, .ps__rail-x {
  z-index: 1000;
}
